import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import '../index.css';

const StorePage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:3001/products/electronics')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const filteredProducts = products.filter(product =>
    product.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleBuyNow = (product) => {
    navigate('/checkout', { state: { product } });
  };

  return (
    <div className="store-container bg-black min-h-screen text-white">
      <div className="text-center mb-6">
        <Link to="/" className="text-3xl font-bold text-red-600 hover:underline">
          Hardware Marketplace
        </Link>
      </div>
      <div className="mb-4 flex justify-center">
        <input
          type="text"
          placeholder="Search products..."
          className="p-2 border border-gray-300 rounded-lg w-1/2 text-black"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="product-list">
        {filteredProducts.map(product => (
          <div key={product.id} className="product-card">
            <img src={product.image} alt={product.title} />
            <h2 className="text-md font-semibold mb-1">{product.title}</h2>
            <p className="text-gray-600 mb-1">${product.price}</p>
            <div className="product-actions">
              <button className="view-details">View Details</button>
              <button className="buy-now" onClick={() => handleBuyNow(product)}>Buy Now</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StorePage;
