import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
  const navigate = useNavigate();
  const [isRegister, setIsRegister] = useState(false);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <h1 className="text-3xl font-bold mb-6 text-center">Welcome to Hardware Marketplace</h1>
      <div className="flex flex-col items-center space-y-4 w-full max-w-xs">
        <button
          className="px-6 py-2 bg-blue-500 text-white font-semibold rounded-lg hover:bg-blue-600 w-full"
          onClick={() => {
            navigate('/register');
            setIsRegister(true);
          }}
        >
          Create Account
        </button>
        <button
          className="px-6 py-2 bg-green-500 text-white font-semibold rounded-lg hover:bg-green-600 w-full"
          onClick={() => {
            navigate('/login');
            setIsRegister(false);
          }}
        >
          Sign In
        </button>
        <button
          className="px-6 py-2 bg-gray-500 text-white font-semibold rounded-lg hover:bg-gray-600 w-full"
          onClick={() => navigate('/store')}
        >
          Continue as Guest
        </button>
      </div>
      {isRegister !== null && (
        <div className="mt-4 flex space-x-4">
          <button
            className="text-blue-600 hover:underline"
            onClick={() => setIsRegister(!isRegister)}
          >
            {isRegister ? 'Already have an account? Sign in' : 'Need an account? Register'}
          </button>
          <button
            className="text-gray-600 hover:underline"
            onClick={() => navigate('/store')}
          >
            Continue as Guest
          </button>
        </div>
      )}
    </div>
  );
};

export default HomePage;
