import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../index.css'; // Ensure styles are applied globally

const AuthForm = () => {
  const navigate = useNavigate();
  const [isRegister, setIsRegister] = useState(false);
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const endpoint = isRegister ? 'register' : 'login';
    const payload = isRegister ? { email, username, password } : { username, password };

    try {
      const response = await axios.post(`http://localhost:3001/api/auth/${endpoint}`, payload);
      setMessage(response.data.message);
    } catch (error) {
      setMessage(error.response?.data?.error || 'Something went wrong');
    }
  };

  return (
    <div className="auth-container">
      <Link to="/" className="home-link">Hardware Marketplace</Link>
      <h2 className="auth-title">{isRegister ? 'Create Account' : 'Sign In'}</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        {isRegister && (
          <input
            type="email"
            placeholder="Email"
            className="auth-input"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        )}
        <input
          type="text"
          placeholder="Username"
          className="auth-input"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          required
        />
        <input
          type="password"
          placeholder="Password"
          className="auth-input"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
        />
        <button type="submit" className="auth-button">
          {isRegister ? 'Register' : 'Login'}
        </button>
      </form>
      <div className="auth-options">
        <p onClick={() => setIsRegister(!isRegister)} className="auth-link">
          {isRegister ? 'Already have an account? Sign in' : 'Need an account? Register'}
        </p>
        <button className="auth-guest" onClick={() => navigate('/store')}>Continue as Guest</button>
      </div>
      {message && <p className="auth-message">{message}</p>}
    </div>
  );
};

export default AuthForm;
