import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AuthForm = () => {
  const navigate = useNavigate();
  const [isRegister, setIsRegister] = useState(false);
  const [email, setEmail] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isRegister ? 'register' : 'login';
    const payload = isRegister ? { email, username, password } : { username, password };

    try {
      const response = await axios.post(`http://localhost:3001/api/auth/${endpoint}`, payload);
      setMessage(response.data.message);

      if (response.data.username) {
        localStorage.setItem('username', response.data.username);
        navigate('/store'); // Redirect user after successful login/registration
      }
    } catch (error) {
      setMessage(error.response?.data?.error || 'Something went wrong');
    }
  };

  return (
    <div className="auth-container">
      <h2>{isRegister ? 'Create Account' : 'Sign In'}</h2>
      <form onSubmit={handleSubmit}>
        {isRegister && (
          <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        )}
        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} required />
        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">{isRegister ? 'Register' : 'Login'}</button>
      </form>
      <p onClick={() => setIsRegister(!isRegister)} className="toggle-auth">
        {isRegister ? 'Already have an account? Sign in' : 'Need an account? Register'}
      </p>
      {message && <p className="auth-message">{message}</p>}
    </div>
  );
};

export default AuthForm;
