import React, { useState, useEffect } from 'react';

const StorePage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/products/electronics')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const filteredProducts = products.filter(product =>
    product.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-4 bg-gray-100 min-h-screen">
      <h1 className="text-2xl font-bold mb-4 text-center">Electronics Store</h1>
      <div className="mb-4 flex justify-center">
        <input
          type="text"
          placeholder="Search products..."
          className="p-2 border border-gray-300 rounded-lg w-1/2"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 justify-center">
        {filteredProducts.map(product => (
          <div
            key={product.id}
            className="p-3 bg-white rounded-lg shadow hover:shadow-lg transition flex flex-col items-center w-60"
          >
            <img
              src={product.image}
              alt={product.title}
              className="w-40 h-32 object-cover mb-3 rounded"
            />
            <h2 className="text-md font-semibold mb-1 text-center">{product.title}</h2>
            <p className="text-gray-600 mb-1">${product.price}</p>
            <button className="px-3 py-1 bg-blue-500 text-white rounded-lg hover:bg-blue-600 w-full">
              View Details
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StorePage;
