import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const StorePage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/api/products/electronics') // ✅ Ensure this is the correct API route
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const filteredProducts = products.filter(product =>
    product.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-4 min-h-screen bg-black text-white">
      <div className="mb-4 flex justify-between items-center">
        <Link to="/" className="text-4xl font-bold text-white">
          Hardware Marketplace
        </Link>
      </div>
      <h1 className="text-3xl font-bold mb-4 text-center text-red-500">Electronics Store</h1>
      <div className="mb-4 flex justify-center">
        <input
          type="text"
          placeholder="Search products..."
          className="p-2 border border-gray-300 rounded-lg w-1/2 text-black"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      <div className="flex flex-wrap justify-center gap-6">
        {filteredProducts.length > 0 ? (
          filteredProducts.map(product => (
            <div key={product.id} className="p-3 bg-gray-800 rounded-lg shadow hover:shadow-lg transition flex flex-col items-center w-64">
              <img src={product.image} alt={product.title} className="w-40 h-40 object-cover mb-3 rounded" />
              <h2 className="text-md font-semibold mb-1 text-center">{product.title}</h2>
              <p className="text-yellow-400 mb-1">${product.price}</p>
              <div className="flex space-x-2">
                <button className="px-3 py-1 bg-blue-500 text-white rounded-lg hover:bg-blue-600 w-full">
                  View Details
                </button>
                <button className="px-3 py-1 bg-green-500 text-white rounded-lg hover:bg-green-600 w-full">
                  Buy Now
                </button>
              </div>
            </div>
          ))
        ) : (
          <p className="text-center text-red-500">No products found.</p>
        )}
      </div>
    </div>
  );
};

export default StorePage;
