import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import '../index.css';

const StorePage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetch('http://localhost:3001/products/electronics')
      .then(response => response.json())
      .then(data => setProducts(data))
      .catch(error => console.error('Error fetching products:', error));
  }, []);

  const filteredProducts = products.filter(product =>
    product.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="p-4 bg-black min-h-screen text-white">
      
      <Link to="/" className="text-3xl font-bold mb-6 text-center text-red-600 hover:underline block">
        Hardware Marketplace
      </Link>
      
      <div className="mb-4 flex justify-center">
        <input
          type="text"
          placeholder="Search products..."
          className="p-2 border border-gray-300 rounded-lg w-1/2 text-black"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>
      
      <div className="flex overflow-x-auto space-x-6 p-4">
        {filteredProducts.map(product => (
          <div
            key={product.id}
            className="p-3 bg-white rounded-lg shadow hover:shadow-lg transition flex flex-col items-center w-64 text-black flex-shrink-0"
          >
            <img
              src={product.image}
              alt={product.title}
              className="w-40 h-40 object-cover mb-3 rounded"
            />
            <h2 className="text-md font-semibold mb-1 text-center">{product.title}</h2>
            <p className="text-gray-600 mb-1">${product.price}</p>
            <div className="flex space-x-2">
              <button className="px-3 py-1 bg-blue-500 text-white rounded-lg hover:bg-yellow-500 hover:text-black">
                View Details
              </button>
              <button className="px-3 py-1 bg-green-500 text-white rounded-lg hover:bg-yellow-500 hover:text-black">
                Buy Now
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default StorePage;
